require "test-unit"
require "./Q752.rb"

# Definition for a binary tree node.

# @param {TreeNode} root
# @return {Integer[]}
#
#
class TestOpenLock < Test::Unit::TestCase
  

  def test_class_open_lock()
    deadends = ["0201","0101","0102","1212","2002"]
    target = "0202"
    
    assert_equal(6,open_lock(deadends,target))

    deadends_2 = ["8888"]
    target_2 = "0009"
    
    assert_equal(1,open_lock(deadends_2,target_2))
  end

